INSERT INTO `newsletter` (`id_newsletter`, `judul`, `isi`, `tanggal`, `gambar_url`) VALUES
(1, 'IndoBuildTech Bandung 2017', '<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:14px;\">Kunjungi both kami PT. Atap Teduh Lestari yang berada di blok C1 (Belok kanan dari Pintu Masuk) pada acara IndoBuildTech (IBT) exhibition yang diselenggarakan di graha siliwangi bandung pada tanggal 11 hingga 15 Oktober 2017.<br />\r\n	<br />\r\n	Anda dapat mengenal lebih dekat dengan produk kami, bertanya lebih lengkap tentang produk, bahkan konsultasi sesuai dengan kebutuhan anda.<br />\r\n	<br />\r\n	Jangan lewatkan juga Presentasi dan sesi diskusi yang akan dilaksanakan pada hari sabtu pukul 19.00 WIB dengan Tema Produk Palmex dan DUO, serta hari minggu pukul 15.00 dengan tema Produk 1 2 Truss dan Taperroof di samping Booth kami.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<div class=\"row\">\r\n	<div class=\"cols-sm-12\">\r\n		<div class=\"col-sm-4\">\r\n			<div class=\"thumbnail\">\r\n				<a data-gallery=\"example-gallery\" data-toggle=\"lightbox\" href=\"http://roof-online.com/atl/assets/uploads/images/IBT%203.jpg\"><img class=\"img-responsive\" onclick=\"onClick(this)\" src=\"http://roof-online.com/atl/assets/uploads/images/IBT%203.jpg\" /> </a></div>\r\n		</div>\r\n		<div class=\"col-sm-4\">\r\n			<div class=\"thumbnail\">\r\n				<a data-gallery=\"example-gallery\" data-toggle=\"lightbox\" href=\"http://roof-online.com/atl/assets/uploads/images/ibt%20bandung%204.jpg\"><img class=\"img-responsive\" onclick=\"onClick(this)\" src=\"http://roof-online.com/atl/assets/uploads/images/ibt%20bandung%204.jpg\" /> </a></div>\r\n		</div>\r\n		<div class=\"col-sm-4\">\r\n			<div class=\"thumbnail\">\r\n				<a data-gallery=\"example-gallery\" data-toggle=\"lightbox\" href=\"http://roof-online.com/atl/assets/uploads/images/IBT%20Bandung.jpg\"><img class=\"img-responsive\" onclick=\"onClick(this)\" src=\"http://roof-online.com/atl/assets/uploads/images/IBT%20Bandung.jpg\" /> </a></div>\r\n		</div>\r\n		<div class=\"col-sm-4\">\r\n			<div class=\"thumbnail\">\r\n				<a data-gal